    <input type="file" accept=".csv" onChange={handleFileUpload} />
    <table>
      <thead>
        <tr>
          <th>Column 1</th>
          <th>Column 2</th>
          <th>Column 3</th>
          <th>Column 4</th>
          <th>Column 5</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row, index) => (
          <tr key={index}>
            <td>{row.Column1}</td>
            <td>{row.Column2}</td>
            <td>{row.Column3}</td>
            <td>{row.Column4}</td>
            <td>{row.Column5}</td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
  );
}

export default App;
