import React, { useState } from 'react';
import Papa from 'papaparse';

function App() {
  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 10;

  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (result) => {
        const trimmedData = result.data.map((row) => {
          const limitedRow = {};
          const keys = Object.keys(row).slice(0, 5); // Берем первые 5 ключей (столбцов)
          keys.forEach((key) => {
            limitedRow[key] = row[key];
          });
          return limitedRow;
        });

        setData(trimmedData);
      },
    });
  };

  const totalPages = Math.ceil(data.length / rowsPerPage);
  const currentData = data.slice((currentPage - 1) * rowsPerPage, currentPage * rowsPerPage);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>CSV Upload</h1>
      <input type="file" accept=".csv" onChange={handleFileUpload} />

      <div style={{ marginTop: '20px' }}>
        {currentData.length > 0 && (
          <div style={{ display: 'flex', fontWeight: 'bold', borderBottom: '1px solid #ccc', paddingBottom: '10px' }}>
            {Object.keys(currentData[0]).map((key) => (
              <div style={{ flex: 1, padding: '0 10px' }} key={key}>{key}</div>
            ))}
          </div>
        )}
        {currentData.map((row, index) => (
          <div
            key={index}
            style={{
              display: 'flex',
              padding: '10px 0',
              borderBottom: '1px solid #eee',
            }}
          >
            {Object.values(row).map((val, i) => (
              <div style={{ flex: 1, padding: '0 10px' }} key={i}>{val}</div>
            ))}
          </div>
        ))}
      </div>

      <div style={{ marginTop: '20px' }}>
        <button 
          onClick={() => handlePageChange(currentPage - 1)} 
          disabled={currentPage === 1}
          style={{ marginRight: '10px' }}
        >
          Previous
        </button>
        <span>Page {currentPage} of {totalPages}</span>
        <button 
          onClick={() => handlePageChange(currentPage + 1)} 
          disabled={currentPage === totalPages}
          style={{ marginLeft: '10px' }}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default App;
